package LC;

public class LC468 {
    public static void main(String[] args) {
        System.out.println(validIPAddress("..."));
    }
    public static String validIPAddress(String queryIP) {
         if(queryIP.indexOf('.')>=0){
             return isIpv4(queryIP)?"IPv4":"Neither";
         }else {

             return isIpv6(queryIP)?"IPv6":"Neither";
         }


    }
    public  static boolean isIpv4(String queryIP){
        if(!queryIP.matches("\\d+\\.\\d+\\.\\d+\\.\\d+")) return false;
        String[] split = queryIP.split("\\.");
        for (String s : split) {
            if(s.length()>3||Integer.valueOf(s)>255||s.charAt(0)=='0'&&s.length()!=1) return false;
        }
        return true;
    }
    public static boolean isIpv6(String queryIP){
        String[] split = queryIP.split(":", -1);
        if(split.length!=8) return false;
        for (String s : split) {
            if(s.length()>4||s.length()==0) return false;
            for (int i = 0; i <s.length() ; i++) {
                if(!(Character.isDigit(s.charAt(i))||Character.toLowerCase(s.charAt(i))>='a'&&Character.toLowerCase(s.charAt(i))<='f')){
                    return false;
                }
            }
        }
        return true;


    }
}
